New-TransportRule -Name "Visual Cue - External to Organization" -Priority 0 -FromScope "NotInOrganization" -ApplyHtmlDisclaimerLocation "Prepend" -Mode "Audit" -ApplyHtmlDisclaimerText "<div style=""background-color:#FFEB9C; width:100%; border-style: solid; border-color:#9C6500; border-width:1pt; padding:2pt; font-size:10pt; line-height:12pt; font-family:'Calibri'; color:Black; text-align: left;""><span style=""color:#9C6500; font-weight:bold;"">CAUTION:</span> This email originated from outside of the organization.  Do not click links or open attachments unless you recognize the sender and know the content is safe.</div><br>"

New-TransportRule -Name "Visual Cue - No SPF Validation" -Priority 1 -HeaderContainsMessageHeader "Authentication-Results" -HeaderContainsWords "spf=TempError","spf=PermError","spf=None","spf=Neutral","spf=SoftFail","spf=Fail" -ApplyHtmlDisclaimerLocation "Prepend" -Mode "Audit" -ApplyHtmlDisclaimerText "<div style=""background-color:#FFC7CE; width:100%; border-style: solid; border-color:#9C0006; border-width:1pt; padding:2pt; font-size:10pt; line-height:12pt; font-family:'Calibri'; color:Black; text-align: left; margin-bottom: 2pt;""><span style=""color:#9C0006; font-weight:bold;"">WARNING:</span> The sender of this email could not be validated and may not match the person in the ""From"" field.</div>"